import { Component } from '@angular/core';

@Component({
  selector: 'body',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'portfolio-angular';
  dark: boolean = true;
  handleDark() {
    this.dark = !this.dark;
    localStorage.setItem("dark", JSON.stringify(this.dark))
  }
  ngOnInit() {
    if (localStorage.getItem("dark")) {
      this.dark = JSON.parse(localStorage.getItem("dark") || "")
    } else {
      localStorage.setItem("dark", JSON.stringify(this.dark))
    }
  }
}
<app-header [dark]="dark"
(changeDark)="handleDark()"></app-header>
<main [ngClass]="{'dark': dark}">
  <router-outlet>
  </router-outlet>
</main>
<footer [ngClass]="{'dark': dark}">
  <h3>Contact:</h3>
  <nav>
    <a class="underline" 
    href="mailto:jacobshomali21@gmail.com">jacobshomali21@gmail.com</a>
    <a class="underline"
    href="linkedin.com/in/jacob-shomali-a206aa1ba ">LinkedIn</a>
  </nav>
</footer>
