import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {
  @Input() dark!: boolean;
  @Output() changeDark: EventEmitter<null> = new EventEmitter();
  constructor() { }

  ngOnInit(): void {
  }
  handleClick() {
    this.changeDark.emit();
  }
}
<header [ngClass]="{'dark': dark}">
    <a routerLink="/">
        <h1 class="underline" >Jacob's Portfolio</h1>
    </a>
    <a class="underline" href="https://github.com/collerhatguy">
        Github
    </a>
    <a class="underline" routerLink="/articles">
        Articles
    </a>
    <div class="btn" (click)="handleClick()">
        <i *ngIf="dark" class="fa-solid fa-moon"></i> 
        <i *ngIf="!dark" class="fa-solid fa-sun"></i>
    </div>
</header>
